{"changed":true,"filter":false,"title":"user_test.rb","tooltip":"/test/models/user_test.rb","value":"require 'test_helper'\n\nclass UserTest < ActiveSupport::TestCase\n  \n  def setup\n    @user = User.new(name: \"Example User\", email: \"user@example.com\",\n                        password: \"foobar\", password_confirmation: \"foobar\")\n  end\n  \n  test \"should be valid\" do\n    assert @user.valid?\n  end\n  \n  test \"name should be present\" do\n    @user.name = \"        \"\n    assert_not @user.valid?\n  end\n  \n  test \"email should be present\" do\n    @user.email = \"        \"\n    assert_not @user.valid?\n  end\n  \n  test \"name should not be to long\" do\n    @user.name = \"a\" * 31\n    assert_not @user.valid?\n  end\n  \n  test \"email should not be to long\" do\n    @user.email = \"a\" * 31 + \"@example.com\"\n    assert_not @user.valid?\n  end\n  \n  test \"email validation should accept all valid addresses\" do\n    valid_addresses = %w[user@example.com USER@foo.COM A_US-ER@foo.bar.org\n                         first.last@foo.jp alice+bob@baz.cn]\n    valid_addresses.each do |valid_address|\n      @user.email = valid_address\n      assert @user.valid?, \"#{valid_address.inspect} should be valid\"\n    end\n  end\n  \n  test \"email validation should reject invalid addresses\" do\n    invalid_addresses = %w[user@example,com user_at_foo.org user.name@example.\n                           foo@bar_baz.com foo@bar+baz.com]\n    invalid_addresses.each do |invalid_address|\n      @user.email = invalid_address\n      assert_not @user.valid?, \"#{invalid_address.inspect} should be invalid\"\n    end\n  end\n  \n  test \"email addresses should be unique\" do\n    duplicate_user = @user.dup\n    duplicate_user.email = @user.email.upcase\n    @user.save\n    assert_not duplicate_user.valid?\n  end\n  \n  test \"email addresses should be saved as lower-case\" do\n    mixed_case_email = \"Foo@ExAMPle.CoM\"\n    @user.email = mixed_case_email\n    @user.save\n    assert_equal mixed_case_email.downcase, @user.reload.email\n  end\n  \n  test \"password should not be too short\" do\n    @user.password = @user.password_confirmation = \"a\" * 5\n    assert_not @user.valid?\n  end\n  \n  \n  \nend\n","undoManager":{"mark":-51,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":59,"column":4},"end":{"row":59,"column":5},"action":"insert","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":5},"end":{"row":59,"column":6},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":6},"end":{"row":59,"column":7},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":7},"end":{"row":59,"column":8},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":8},"end":{"row":59,"column":9},"action":"insert","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":8},"end":{"row":59,"column":9},"action":"remove","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":8},"end":{"row":59,"column":9},"action":"insert","lines":["w"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":9},"end":{"row":59,"column":10},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":10},"end":{"row":59,"column":11},"action":"insert","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":10},"end":{"row":59,"column":11},"action":"remove","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":9},"end":{"row":59,"column":10},"action":"remove","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":8},"end":{"row":59,"column":9},"action":"remove","lines":["w"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":7},"end":{"row":59,"column":8},"action":"remove","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":59,"column":6},"end":{"row":59,"column":7},"action":"remove","lines":["s"]}]}],[{"group"